package CoreJava;

public class Pat3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<10; i++) {
			System.out.print(" X");
		}
		System.out.println();
		
		for(int a=1; a<9; a++) {
			for(int j=0; j<+a; j++) {
				System.out.print("  ");
			}
			System.out.println("X");
		}
		
		for(int i=1; i<10; i++) {
			System.out.print(" X");
		}
		System.out.println();
	}
}
